{  
  // Calculate suggested number of CPUs depending of the input reads size
  if ($job.inputs.input_reads.constructor == Array){
    if ($job.inputs.input_reads[1]){
      reads_size = $job.inputs.input_reads[0].size + $job.inputs.input_reads[1].size
    } else{
      reads_size = $job.inputs.input_reads[0].size
    }
  }
  else{
    reads_size = $job.inputs.input_reads.size
  }
  if(!reads_size) { reads_size = 0 }


  GB_1 = 1024*1024*1024
  if(reads_size < GB_1){ suggested_cpus = 1 }
  else if(reads_size < 10 * GB_1){ suggested_cpus = 8 }
  else { suggested_cpus = 31 }

  if($job.inputs.reserved_threads){ return $job.inputs.reserved_threads }
  else if($job.inputs.threads){ return $job.inputs.threads } 
  else if($job.inputs.sambamba_threads) { return $job.inputs.sambamba_threads }
  else{    return suggested_cpus  }
}

