{
  reference_file = $job.inputs.reference_index_tar.path.split('/')[$job.inputs.reference_index_tar.path.split('/').length-1]
  name = reference_file.slice(0, -4) // cut .tar extension 

  name_list = name.split('.')
  ext = name_list[name_list.length-1]

  if (ext == 'gz' || ext == 'GZ'){
    a = name_list.pop() // strip fasta.gz
    a = name_list.pop()
  } else
    a = name_list.pop() //strip only fasta/fa

  return name_list.join('.')

}

valueFrom.script:
{ 
     ///////////////////////////////////////////
    ///  SAMBAMBA VIEW   //////////////////////
   ///////////////////////////////////////////
function common_substring(a,b) {
  var i = 0;

  while(a[i] === b[i] && i < a.length)
  {
    i = i + 1;
  }

  return a.slice(0, i);
}

   // Set output file name
  if($job.inputs.input_reads[0] instanceof Array){
    input_1 = $job.inputs.input_reads[0][0] // scatter mode
    input_2 = $job.inputs.input_reads[0][1]
  } else if($job.inputs.input_reads instanceof Array){
    input_1 = $job.inputs.input_reads[0]
    input_2 = $job.inputs.input_reads[1]
  }else {
    input_1 = [].concat($job.inputs.input_reads)[0]
    input_2 = input_1
  }
  full_name = input_1.path.split('/')[input_1.path.split('/').length-1] 

  if($job.inputs.output_name){name = $job.inputs.output_name }
  else if ($job.inputs.input_reads.length == 1){ 
    name = full_name

    if(name.slice(-3, name.length) === '.gz' || name.slice(-3, name.length) === '.GZ')
      name = name.slice(0, -3)   
    if(name.slice(-3, name.length) === '.fq' || name.slice(-3, name.length) === '.FQ')
      name = name.slice(0, -3)
    if(name.slice(-6, name.length) === '.fastq' || name.slice(-6, name.length) === '.FASTQ')
      name = name.slice(0, -6)

  }else{
    full_name2 = input_2.path.split('/')[input_2.path.split('/').length-1] 
    name = common_substring(full_name, full_name2)

    if(name.slice(-1, name.length) === '_' || name.slice(-1, name.length) === '.')
      name = name.slice(0, -1)
    if(name.slice(-2, name.length) === 'p_' || name.slice(-1, name.length) === 'p.')
      name = name.slice(0, -2)
    if(name.slice(-2, name.length) === 'P_' || name.slice(-1, name.length) === 'P.')
      name = name.slice(0, -2)
    if(name.slice(-3, name.length) === '_p_' || name.slice(-3, name.length) === '.p.')
      name = name.slice(0, -3)
    if(name.slice(-3, name.length) === '_pe' || name.slice(-3, name.length) === '.pe')
      name = name.slice(0, -3)
  }

  // Read number of threads if defined
  if ($job.inputs.sambamba_threads){
    threads = $job.inputs.sambamba_threads
  }
  else if ($job.inputs.threads){
    threads = $job.inputs.threads
  }
  else if ($job.inputs.wgs_hg38_mode_threads){
    MAX_THREADS = 36
    ref_name_arr = $job.inputs.reference_index_tar.path.split('/')
    ref_name = ref_name_arr[ref_name_arr.length - 1]
    if (ref_name.search('38') >= 0){threads = $job.inputs.wgs_hg38_mode_threads}
    else {threads = MAX_THREADS}
  }
  else { threads = 8 }

  if ($job.inputs.filter_out_secondary_alignments){
    filt_sec = ' --filter \'not secondary_alignment\' '
  }
  else {filt_sec=' '}

  // Set output command
  sambamba_path = '/opt/sambamba_v0.6.0'
  if ($job.inputs.output_format == 'BAM') {
    return "| " + sambamba_path + " view -t "+ threads + filt_sec + "-f bam -S /dev/stdin -o "+ name + ".bam"
  }
  else if ($job.inputs.output_format == 'SAM'){ // SAM
    return "> " + name + ".sam"
  }
  else { // SortedBAM is considered default
    return "| " + sambamba_path + " view -t "+ threads + filt_sec + "-f bam -l 0 -S /dev/stdin"
  }

}

{
  ///////////////////////////////////////////
 ///  SAMBAMBA SORT   //////////////////////
///////////////////////////////////////////

function common_substring(a,b) {
  var i = 0;
  while(a[i] === b[i] && i < a.length)
  {
    i = i + 1;
  }

  return a.slice(0, i);
}

   // Set output file name
