    input_2 = $job.inputs.input_reads[1]
  }else {
    input_1 = [].concat($job.inputs.input_reads)[0]
    input_2 = input_1
  }
  full_name = input_1.path.split('/')[input_1.path.split('/').length-1] 

  if($job.inputs.output_name){name = $job.inputs.output_name }
  else if ($job.inputs.input_reads.length == 1){
    name = full_name
    if(name.slice(-3, name.length) === '.gz' || name.slice(-3, name.length) === '.GZ')
      name = name.slice(0, -3)   
    if(name.slice(-3, name.length) === '.fq' || name.slice(-3, name.length) === '.FQ')
      name = name.slice(0, -3)
    if(name.slice(-6, name.length) === '.fastq' || name.slice(-6, name.length) === '.FASTQ')
      name = name.slice(0, -6)

  }else{
    full_name2 = input_2.path.split('/')[input_2.path.split('/').length-1] 
    name = common_substring(full_name, full_name2)

    if(name.slice(-1, name.length) === '_' || name.slice(-1, name.length) === '.')
      name = name.slice(0, -1)
    if(name.slice(-2, name.length) === 'p_' || name.slice(-1, name.length) === 'p.')
      name = name.slice(0, -2)
    if(name.slice(-2, name.length) === 'P_' || name.slice(-1, name.length) === 'P.')
      name = name.slice(0, -2)
    if(name.slice(-3, name.length) === '_p_' || name.slice(-3, name.length) === '.p.')
      name = name.slice(0, -3)
    if(name.slice(-3, name.length) === '_pe' || name.slice(-3, name.length) === '.pe')
      name = name.slice(0, -3)
  }

  //////////////////////////
  // Set sort memory size

  reads_size = 0 // Not used because of situations when size does not exist!
  GB_1 = 1024*1024*1024
  if(reads_size < GB_1){ 
    suggested_memory = 4
    suggested_cpus = 1
  }
  else if(reads_size < 10 * GB_1){ 
    suggested_memory = 15
    suggested_cpus = 8
  }
  else { 
    suggested_memory = 58 
    suggested_cpus = 31
  }


  if(!$job.inputs.total_memory){ total_memory = suggested_memory }
  else{ total_memory = $job.inputs.total_memory }

  // TODO:Rough estimation, should be fine-tuned!
  if(total_memory > 16){ sorter_memory = parseInt(total_memory / 3) }
  else{ sorter_memory = 5 }

  if ($job.inputs.sort_memory){
    sorter_memory_string = $job.inputs.sort_memory +'GiB'
  }
  else sorter_memory_string = sorter_memory + 'GiB' 

  // Read number of threads if defined
  if ($job.inputs.sambamba_threads){
    threads = $job.inputs.sambamba_threads
  }
  else if ($job.inputs.threads){
    threads = $job.inputs.threads
  }
  else if ($job.inputs.wgs_hg38_mode_threads){
    MAX_THREADS = 36
    ref_name_arr = $job.inputs.reference_index_tar.path.split('/')
    ref_name = ref_name_arr[ref_name_arr.length - 1]
    if (ref_name.search('38') >= 0){threads = $job.inputs.wgs_hg38_mode_threads}
    else {threads = MAX_THREADS}
  }
  else { threads = 8 }


  sambamba_path = '/opt/sambamba_v0.6.0'

  // Coordinate Sorted BAM is default
  if (!(($job.inputs.output_format == 'BAM') || ($job.inputs.output_format == 'SAM'))){
    append = ''
    if ($job.inputs.output_format == 'Queryname Sorted BAM'){
		append = ' --sort-by-name'
    }
    else if($job.inputs.output_format == 'Natural Sorted BAM'){
      	append = ' --natural-sort'
    }
    cmd = "| " + sambamba_path + " sort -t " + threads + append
    return cmd + " -m " + sorter_memory_string + " --tmpdir ./ -o " + name + ".bam -l 5 /dev/stdin"
  }
  else return ""
}

