{

  if($job.inputs.read_group_header){
  	return '-R ' + $job.inputs.read_group_header
  }

  function add_param(key, val){
    if(!val){
      return
	}
    param_list.push(key + ':' + val)
  }

  param_list = []

  // Set output file name
  if($job.inputs.input_reads[0] instanceof Array){
    input_1 = $job.inputs.input_reads[0][0] // scatter mode
  } else if($job.inputs.input_reads instanceof Array){
    input_1 = $job.inputs.input_reads[0]
  }else {
    input_1 = [].concat($job.inputs.input_reads)[0]
  }

  //Read metadata for input reads
  read_metadata = input_1.metadata
  if(!read_metadata) read_metadata = []

  if($job.inputs.rg_id){
    add_param('ID', $job.inputs.rg_id)
  }
  else {
    add_param('ID', '1')
  } 


  if($job.inputs.rg_data_submitting_center){
  	add_param('CN', $job.inputs.rg_data_submitting_center)
  }
  else if('data_submitting_center' in  read_metadata){
  	add_param('CN', read_metadata.data_submitting_center)
  }

  if($job.inputs.rg_library_id){
  	add_param('LB', $job.inputs.rg_library_id)
  }
  else if('library_id' in read_metadata){
  	add_param('LB', read_metadata.library_id)
  }

  if($job.inputs.rg_median_fragment_length){
  	add_param('PI', $job.inputs.rg_median_fragment_length)
  }


  if($job.inputs.rg_platform){
  	add_param('PL', $job.inputs.rg_platform)
  }
  else if('platform' in read_metadata){
    if(read_metadata.platform == 'HiSeq X Ten'){
      rg_platform = 'Illumina'
    }
    else{
      rg_platform = read_metadata.platform
    }
  	add_param('PL', rg_platform)
  }

  if($job.inputs.rg_platform_unit_id){
  	add_param('PU', $job.inputs.rg_platform_unit_id)
  }
  else if('platform_unit_id' in read_metadata){
  	add_param('PU', read_metadata.platform_unit_id)
  }

  if($job.inputs.rg_sample_id){
  	add_param('SM', $job.inputs.rg_sample_id)
  }
  else if('sample_id' in  read_metadata){
  	add_param('SM', read_metadata.sample_id)
  }

  return "-R '@RG\\t" + param_list.join('\\t') + "'"

}

{
  /////// Set input reads in the correct order depending of the paired end from metadata

     // Set output file name
  if($job.inputs.input_reads[0] instanceof Array){
    input_reads = $job.inputs.input_reads[0] // scatter mode
  } else {
    input_reads = $job.inputs.input_reads = [].concat($job.inputs.input_reads)
  }


  //Read metadata for input reads
  read_metadata = input_reads[0].metadata
  if(!read_metadata) read_metadata = []

  order = 0 // Consider this as normal order given at input: pe1 pe2

  // Check if paired end 1 corresponds to the first given read
  if(read_metadata == []){ order = 0 }
  else if('paired_end' in  read_metadata){ 
    pe1 = read_metadata.paired_end
    if(pe1 != 1) order = 1 // change order
  }

  // Return reads in the correct order
  if (input_reads.length == 1){
    return input_reads[0].path // Only one read present
  }
  else if (input_reads.length == 2){
    if (order == 0) return input_reads[0].path + ' ' + input_reads[1].path
    else return input_reads[1].path + ' ' + input_reads[0].path
  }

}

