{


  if($job.inputs.output_reads_not_selected_by_filter)
  {
       filepath = $job.inputs.input_bam_or_sam_file.path

       filename = filepath.split("/").pop();

       file_dot_sep = filename.split(".");
       file_ext = file_dot_sep[file_dot_sep.length-1];

       new_filename = filename.substr(0,filename.lastIndexOf("."));

       extension = '.sam';

       if ((typeof $job.inputs.output_BAM !== "undefined" && $job.inputs.output_BAM === true) ||
           (typeof $job.inputs.Output_uncompressed_BAM !== "undefined" && $job.inputs.Output_uncompressed_BAM === true) ||
